// Define name of URL input for easy reference
const urlInputName = "urlInput";
const resultTargetName = "resultTarget";

// Define the function to fetch URL and show result
async function fetchUrl() {
    let text;
    try {
        const url = (await read(urlInputName)).text; // Get URL from user input
        const response = await fetch(url); // Fetch the URL
        text = await response.text(); // Get text content from response
    } catch (error) {
        text = "Error: " + error.message; // Set text to error message
    }

    await update(resultTargetName, null, {text, hide: false}); // Update text element and make it visible
}

// Show URL input
await show(createInput(textInputType, {name: urlInputName, placeholder: "Enter URL here..."}), {noAccept: true});

// Create fetch button
const fetchButton = createContainer(buttonType, createText(paragraphType, "Fetch URL"), {onClick: fetchUrl});
const fetchButtonWrapper = createFloatRightWrapper(fetchButton);

// Show hidden text element for later use
await show(createText(paragraphType, "", {bordered: true, hide: true, name: resultTargetName}));

await show(fetchButtonWrapper, {insertAt: -2}); // Show button as second last element (before text element)

await forever();
