// Show URL input
const urlInput = createInput(textInputType, {placeholder: "Enter URL here..."});
await show(urlInput, {noAccept: true});

// Show hidden text element for later use
const resultTarget = createText(paragraphType, "", {bordered: true, hide: true});
await show(resultTarget);

// Define the function to fetch URL and show result
async function fetchUrl() {
    let text;
    try {
        const url = (await read(urlInput.id)).text; // Get URL from user input
        const response = await fetch(url); // Fetch the URL
        text = await response.text(); // Get text content from response
    } catch (error) {
        text = "Error: " + error.message; // Set text to error message
    }

    await update(resultTarget.id, {text, hide: false}); // Update text element and make it visible
}

// Show fetch button
const fetchButton = createButton(createText(paragraphType, "Fetch URL"), fetchUrl);
const fetchButtonWrapper = createFloatRightWrapper(fetchButton);
await show(fetchButtonWrapper, {insertBefore: resultTarget.id}); // Show button before result target

await forever();
