// Create an image input element to allow uploading images
const fileInput = createInput(fileInputType, {
    placeholder: "Upload your images here...",
    multiple: true,  // Allow multiple image uploads
    allowedExtensions: [".png", ".jpg", ".jpeg", ".webp", ".gif", ".bmp"], // Allow standard image formats
    allowedMimeTypes: ["image/*"],
});

// Show the image input to the user and wait for them to accept the uploaded images
const uploadedImages = await show(fileInput);

// Process each uploaded image
if (uploadedImages && uploadedImages.files && uploadedImages.files.length > 0) {
    for (let file of uploadedImages.files) {
        // Show the image element as image input so user can see and edit it
        await show(createInput(imageInputType, {
            defaultValue: file.dataURL,
        }), { noAccept: true, });
    }
}
