// Display a title for the tool
await show(createTitle("Live Formula Calculator"));

// Create an input box for the user to input their formula
const formulaInput = createInput(codeInputType, {
    language: "javascript",
    placeholder: "Enter a formula or code... (e.g. 5+3*2)",
    onValidate: () => {
        liveUpdate();
    }
});

// Add a container to display the result
const resultText = createParagraph("Result will appear here..."); 

// Show the text input and result display
await show(createGroup([formulaInput, resultText]), { noAccept: true });

// Function to calculate the formula and update the result
async function liveUpdate() {
    const formula = (await read(formulaInput.id)).code; // Read the current formula input
    try {
        const result = eval(formula); // Evaluate the formula
        // Update the result display with the evaluated result
        await update(resultText.id, { text: `Result:\n${result}` });
    } catch (error) {
        // If an error occurs in eval (e.g. invalid formula), we update with an error message.
        await update(resultText.id, { text: "Invalid formula." });
    }
}

await forever();
