// Function to update the counters
function updateCounters(input, allInputs) {
    const text = input.text;
    const characterCount = text.length;
    const wordCount = text.trim().split(/\s+/).filter(Boolean).length;
    const lineCount = text.split(/\r?\n/).length;

    return { characterCount, wordCount, lineCount };
}

// Create text input field
const textInput = createInput(textInputType, {
    placeholder: "Enter text here...",
    onValidate: (input, allInputs) => {
        const counters = updateCounters(input, allInputs);
        update(infoElement.id, {
            text: `Characters: ${counters.characterCount}, Words: ${counters.wordCount}, Lines: ${counters.lineCount}`
        });
    }
});

// Create info element to display the counters
const infoElement = createInfo("Characters: 0, Words: 0, Lines: 0");

// Show the text input field and info element with noAccept option
await show(createGroup([textInput, infoElement]), { noAccept: true });

// Await indefinitely
await forever();
